package com.trabajo.parcial.config;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.core.userdetails.User;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.security.core.userdetails.UserDetailsService;
import org.springframework.security.provisioning.InMemoryUserDetailsManager;
import org.springframework.security.web.SecurityFilterChain;
import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;
import org.springframework.security.crypto.password.PasswordEncoder;

@Configuration

// Clase que configura la seguridad de la aplicación
public class SecurityConfig {
    @Bean
    public SecurityFilterChain filterChain(HttpSecurity http) throws Exception {
        http
            .authorizeHttpRequests(auth -> auth
                .requestMatchers("/h2-console/**").permitAll()
                .anyRequest().authenticated()
            )
            .csrf(csrf -> csrf.disable())
            .headers(headers -> headers.disable())
            .httpBasic(httpBasic -> {});
        return http.build();
    }

    @Bean
    public PasswordEncoder passwordEncoder() {
        return new BCryptPasswordEncoder();
    }

    // Configuración de usuario en memoria para autenticación básica
    @Bean
    public UserDetailsService userDetailsService(PasswordEncoder passwordEncoder) {
        UserDetails user = User.builder()
            .username("EIA") // Nombre de usuario
            .password(passwordEncoder.encode("EIA")) // Contraseña
            .roles("USER") // Rol del usuario
            .build(); // Construye el usuario con los detalles especificados
        return new InMemoryUserDetailsManager(user);
    }
}

package com.trabajo.parcial.config;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.core.userdetails.User;
import org.springframework.security.core.userdetails.UserDetails;
import org.springframework.security.core.userdetails.UserDetailsService;
import org.springframework.security.provisioning.InMemoryUserDetailsManager;
import org.springframework.security.web.SecurityFilterChain;
import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;
import org.springframework.security.crypto.password.PasswordEncoder;

@Configuration

// Clase que configura la seguridad de la aplicación
public class SecurityConfig {
    @Bean
    public SecurityFilterChain filterChain(HttpSecurity http) throws Exception {
        // Configura la seguridad HTTP para la aplicación
        http
            // Permite el acceso libre a la consola H2 y requiere autenticación para el resto
            .authorizeHttpRequests(auth -> auth
                .requestMatchers("/h2-console/**").permitAll() // Permite acceso sin autenticación a la consola H2
                .anyRequest().authenticated() // Requiere autenticación para cualquier otro endpoint
            )
            // Desactiva la protección CSRF (necesario para H2 y APIs REST)
            .csrf(csrf -> csrf.disable())
            // Desactiva los headers de seguridad para permitir el uso de frames (necesario para H2)
            .headers(headers -> headers.disable())
            // Habilita autenticación HTTP Basic
            .httpBasic(httpBasic -> {});
        return http.build();
    }

    @Bean
    public PasswordEncoder passwordEncoder() {
        // Codificador seguro para contraseñas
        return new BCryptPasswordEncoder();
    }

    // Configura un usuario en memoria para autenticación básica
    @Bean
    public UserDetailsService userDetailsService(PasswordEncoder passwordEncoder) {
        UserDetails user = User.builder()
            .username("EIA") // Usuario para acceder a la API
            .password(passwordEncoder.encode("EIA")) // Contraseña codificada
            .roles("USER") // Rol asignado
            .build(); // Construye el usuario con los detalles especificados
        return new InMemoryUserDetailsManager(user);
    }
}

package com.trabajo.parcial.model;

import jakarta.persistence.*;
import lombok.*;
import java.util.List;

@Entity
@Data
@NoArgsConstructor
@AllArgsConstructor
@Builder
public class Acosador {
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    private String nombre;
    private String apodo;

    @Enumerated(EnumType.STRING)
    private HighSchoolRole highSchoolRole;

    @ManyToOne
    @JoinColumn(name = "clique_id")
    private Clique grupo;

    private String bullyingReason;
    private int levelOfAnnoyance;

    @OneToMany(mappedBy = "acosador", cascade = CascadeType.ALL)
    private List<PlanVenganza> planesVenganza;

    public enum HighSchoolRole {
        JOCK, GOSSIP, NERD_HATER, TEACHER_PET, OTRO
    }

    public void setId(Long id) {
        this.id = id;
    }
}

package com.trabajo.parcial.model;

import jakarta.persistence.*;
import lombok.*;
import java.util.List;

@Entity
@Data
@NoArgsConstructor
@AllArgsConstructor
@Builder
public class Acosador {
    // Identificador único del acosador
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    // Nombre real del acosador
    private String nombre;
    // Apodo del acosador en el instituto
    private String apodo;

    // Rol que tenía el acosador en el instituto
    @Enumerated(EnumType.STRING)
    private HighSchoolRole highSchoolRole;

    // Grupo o camarilla al que pertenece el acosador
    @ManyToOne
    @JoinColumn(name = "grupo_id")
    private Clique grupo;

    // Motivo del bullying
    private String bullyingReason;
    // Nivel de molestia causado (1-10)
    private int levelOfAnnoyance;

    // Lista de planes de venganza asociados a este acosador
    @OneToMany(mappedBy = "acosador", cascade = CascadeType.ALL)
    private List<PlanVenganza> planesVenganza;

    // Enumeración de roles típicos en el instituto
    public enum HighSchoolRole {
        JOCK, GOSSIP, NERD_HATER, TEACHER_PET, OTRO
    }

    public void setId(Long id) {
        this.id = id;
    }
}

